import React,{useState,useEffect} from "react";

const AuthProvider = React.createContext({
  isLoggedIn: false,
  onLogout:()=>{},
  onLogin:()=>{}
});
export const AuthCtxProvider = (props)=>{
    const[isLoggedIn,setIsLoggedIn] = useState(false);
    useEffect(() => {
        const storedUserLoggedInInformation = localStorage.getItem('isLoggedIn');
    
        if (storedUserLoggedInInformation === '1') {
          setIsLoggedIn(true);
        }
      }, []);
    const logoutHandler =()=> {
        localStorage.removeItem('isLoggedIn');
        setIsLoggedIn(false);
    }
    const loginHandler =()=> {
        localStorage.setItem('isLoggedIn', '1');
        setIsLoggedIn(true);
    }
return (
    <AuthProvider.Provider value = {{isLoggedIn:isLoggedIn,onLogout:logoutHandler,onLogin:loginHandler}}>{props.children}</AuthProvider.Provider>
)
}
export default AuthProvider;
